function scrollToTarget(o){if(o.length)return $("html,body").animate({scrollTop:o.offset().top-42},750),!1}function smoothScrolling(){$('a[href*="#"]:not([href="#"])').on("click",function(){if(location.pathname.replace(/^\//,"")===this.pathname.replace(/^\//,"")&&location.hostname===this.hostname){var o=$(this.hash);o=o.length?o:$("[name="+this.hash.slice(1)+"]"),scrollToTarget(o)}})}$(function(){smoothScrolling()});